import PropTypes from 'prop-types';
import { Thumbnail } from 'react-bootstrap';

const Item = ({ text, image }) => (
  <Thumbnail href="#" alt={text} src={image} className="centered">
  </Thumbnail>
  // change src image to be a prop passed in
