import { connect } from 'react-redux';
import Items from '../components/Items';
import { VisibilityFilters } from '../actions';

const getVisibleItems = (items, filters) => {
  return items.filter(t => t.type in filters);
  switch (filters) {
    case VisibilityFilters.SHOW_ALL:
      return items;
    case VisibilityFilters.SHOW_NECKLACES:
      return items.filter(t => t.type === 'necklace');
    case VisibilityFilters.SHOW_BRACELETS:
      return items.filter(t => t.type === 'bracelet');
    case VisibilityFilters.SHOW_EARRINGS:
      return items.filter(t => t.type === 'earrings');
    default:
      throw new Error('Unknown filter: ' + filters);
  }
};

const mapStateToProps = state => ({
  items: getVisibleItems(state.items, state.filters)
});

const mapDispatchToProps = dispatch => ({
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Items);
