import React from 'react';
import PropTypes from 'prop-types';
import { Thumbnail } from 'react-bootstrap';

const Item = ({ text }) => (
  <Thumbnail href="#" onClick={this.handleShowModal} alt={text} src="./necklace.jpg" className="centered">
  </Thumbnail>
  // change src image to be a prop passed in
);

Item.propTypes = {
  text: PropTypes.string.isRequired
};

export default Item;
