import { VisibilityFilters } from '../actions';

const initialState = [VisibilityFilters.NECKLACE, VisibilityFilters.BRACELET, VisibilityFilters.EARRINGS];
const filters = (state = initialState, action) => {
  switch (action.type) {
    case 'TOGGLE_FILTER':
      return [...state, action.filter]; // need to remove if already in there
    default:
      return state;
  }
};

export default filters;
