import { connect } from 'react-redux';
import TodoList from '../components/TodoList';
import { VisibilityFilters } from '../actions';

const getVisibleTodos = (todos, filter) => {
  switch (filter) {
    case VisibilityFilters.SHOW_ALL:
      return todos;
    case VisibilityFilters.NECKLACES_ONLY:
      return todos.filter(t => t.type === 'necklace');
    case VisibilityFilters.BRACELETS_ONLY:
      return todos.filter(t => t.type === 'bracelet');
    case VisibilityFilters.EARRINGS_ONLY:
      return todos.filter(t => t.type === 'earrings');
    default:
      throw new Error('Unknown filter: ' + filter);
  }
};

const mapStateToProps = state => ({
  todos: getVisibleTodos(state.todos, state.visibilityFilter)
});

const mapDispatchToProps = dispatch => ({
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TodoList);
