import { VisibilityFilters } from '../actions';

const initialState = {
  'NECKLACE': true, // change to use constants for strings instead
  'BRACELET': true,
  'EARRINGS': true
};
const filters = (state = initialState, action) => {
  switch (action.type) {
    case 'TOGGLE_FILTER':
      // instead of storing array of filters, store map of 3 keys to T/F values
      if (state.indexOf(action.filter) === -1) {
        return [...state, action.filter];
      } else {
        return state; // need to remove if already in there
      }
    default:
      return state;
  }
};

export default filters;
